import React from 'react';
import Card from 'react-bootstrap/Card';

/**
 *  <h1>Lista de linguagens</h1>

                <ul>
                    {this.state.linguagens.map(item => (
                        <li key={item.id}>
                            <p><b>Nome:</b> {item.name}</p>
                            <p><b>Criador:</b> {item.creator}</p>
                            <p><b>Ano de criação:</b> {item.year}</p>
                        </li>
                    ))}
                </ul> key={item.id} 
 */
class CardTextos extends React.Component {
    constructor(props) {
        super(props);
        this.state = { apiResponse: "" };
    }
    
    callAPI() {
        fetch("localhost:4000/api/curriculo")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }));
    }
    
    componentWillMount() {
        this.callAPI();
    }

    render() {
        return (
               <div>
                 
                    
                        <Card style={{ width: '18rem',boxShadow:'0px 10px 74px -22px rgba(0,0,0,0.84)' }}>
                            <Card.Body>
                          <Card.Title></Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                                <Card.Text>
                                {this.state.apiResponse}
                                Some quick example text to build on the card title and make up the bulk of
                                the card's content.
                            
                                </Card.Text>
                                <Card.Link href="#">Card Link</Card.Link>
                                <Card.Link href="#">Another Link</Card.Link>
                            </Card.Body>
                        </Card>
                  
                    
               </div>
        );
    }
}

export default CardTextos;
